package org.nerdslot.Adapters;import android.content.Context;import android.os.Build;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import androidx.annotation.NonNull;import androidx.viewpager.widget.PagerAdapter;import com.google.firebase.storage.FirebaseStorage;import org.nerdslot.Foundation.Helper.GlideApp;import org.nerdslot.Fragments.RootInterface;import org.nerdslot.Models.Issue.Featured;import org.nerdslot.R;import java.util.ArrayList;public class SliderAdapter extends PagerAdapter implements RootInterface {    private Context ctx;    private ArrayList<String> imageUris;    public SliderAdapter(Context context) {        this.ctx = context;        this.imageUris = new ArrayList<>();    }    @Override    public int getCount() {        return imageUris.size();    }    @Override    public boolean isViewFromObject(@NonNull View view, @NonNull Object object) {        return view.equals(object);    }    @NonNull    @Override    public Object instantiateItem(@NonNull ViewGroup container, int position) {        LayoutInflater inflater = LayoutInflater.from(ctx);        View layout = inflater.inflate(R.layout.slider_view_pager, container, false);        ImageView featuredImages = layout.findViewById(R.id.sliderImage);        GlideApp.with(ctx).load(FirebaseStorage.getInstance().getReferenceFromUrl(imageUris.get(position)))                .into(featuredImages);        container.addView(layout, 0);        return layout;    }    @Override    public void destroyItem(@NonNull ViewGroup container, int position, @NonNull Object object) {        container.removeView((View) object);    }    public void setImageUris(ArrayList<Featured> featuredArrayList) {        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.N)            featuredArrayList.forEach(featured -> imageUris.add(featured.getImage()));        else {            for (Featured featured : featuredArrayList)                imageUris.add(featured.getImage());        }        notifyDataSetChanged();    }}